export default {
  data: function data() {
    return {
      createForm: {
        name: '',
        id: ''
      },
      dialog: {
        title: this.$t('create'),
        labelName: this.$t('create')
      }
    };
  },
  methods: {
    open: function open(item) {
      if (item) {
        this.createForm = {
          id: item.id,
          platformDesc: item.platformDesc,
          platformName: item.platformName,
          platformType: item.platformType
        };
        this.dialog = {
          title: this.$t('editSource'),
          labelName: this.$t('save'),
          buttonType: 'UPDATE'
        };
      } else {
        this.createForm = {
          platformDesc: '',
          platformName: '',
          platformType: ''
        };
        this.dialog = {
          title: this.$t('createSource'),
          labelName: this.$t('create'),
          buttonType: 'CREATE'
        };
      }

      this.$refs.platformDialog.open();
    },
    onConfirmCreateType: function onConfirmCreateType(formName) {
      var _this = this;

      this.$refs[formName].validate(function (valid) {
        if (valid) {
          if (!_this.createForm.platformDesc) {
            _this.$message.error(_this.$t('sourxceDesNoempty'));

            return;
          } else if (!_this.createForm.platformName) {
            _this.$message.error(_this.$t('sourceNameNoempty'));

            return;
          } else if (!_this.createForm.platformType) {
            _this.$message.error(_this.$t('sourceTypeNoempty'));

            return;
          }

          _this.$refs.platformDialog.close();

          if (_this.dialog.buttonType === 'CREATE') {
            _this.$emit('success', {
              data: _this.createForm,
              code: 0
            });
          } else {
            _this.$emit('success', {
              data: _this.createForm,
              code: 1
            });
          }
        }
      });
    }
  }
};
