import resize from '@/views/chart/chartsView/resize'
import { getChartData } from '@/http/api/homeApi'
export default {
  mixins: [resize],
  data () {
    return {
      chart: null
    }
  },
  props: {
    id: {
      type: String,
      required: true
    },
    title: {
      type: String,
      default: ''
    },
    isFull: {
      type: Boolean
    }
  },
  watch: {
    isFull () {
      this.chart.resize()
    }
  },
  mounted () {
    this.getEchart()
  },
  methods: {
    async getEchart () {
      const { id, title } = this
      getChartData()
        .then(res => {
          let dataX = []
          let dataY = []
          res.forEach(item => {
            let date = new Date(item.endDate)
            let month = date.getMonth() + 1
            let day = date.getDate()
            dataX.push(month + '-' + day)
            dataY.push(item[id])
          })
          const fun = {
            amountMoney: () => {
              return new this.$echarts.graphic.LinearGradient(0, 0, 0, 1, [
                { offset: 0, color: 'rgba(35, 82, 188, 0.99)' },
                { offset: 1, color: 'rgba(35, 82, 188, 0.2)' }
              ])
            },
            clickCount: () => {
              return new this.$echarts.graphic.LinearGradient(0, 0, 0, 1, [
                { offset: 0, color: 'rgba(255, 210, 0, 1)' },
                { offset: 1, color: 'rgba(255, 210, 0, 0.3)' }
              ])
            },
            seconds: () => {
              return new this.$echarts.graphic.LinearGradient(0, 0, 0, 1, [
                { offset: 0, color: 'rgba(255, 210, 0, 1)' },
                { offset: 1, color: 'rgba(255, 210, 0, 0.3)' }
              ])
            }
          }
          this.chart = this.$echarts.init(document.getElementById(id))
          const option = {
            title: {
              text: title,
              textStyle: {
                color: '#fff',
                fontSize: 15
              },
              top: '4%',
              left: '2%'
            },
            grid: {
              top: '20%',
              left: '2%',
              bottom: '5%',
              containLabel: true
            },
            tooltip: {
              trigger: 'axis',
              backgroundColor: 'rgba(50, 50, 50, 0.5)'
            },
            xAxis: {
              data: dataX,
              boundaryGap: true,
              axisLabel: {
                show: true,
                textStyle: {
                  color: '#fff', // 更改坐标轴文字颜色
                  fontSize: 12 // 更改坐标轴文字大小
                }
              },
              // axisTick: {
              //   show: false // 隐藏刻度线
              // },
              axisLine: {
                lineStyle: {
                  color: '#fff'
                }
              }
            },
            yAxis: {
              type: 'value',
              axisTick: {
                show: false
              },
              axisLabel: {
                show: true,
                textStyle: {
                  color: '#fff',
                  fontSize: 12
                }
              },
              splitLine: {
                show: false
              },
              axisLine: {
                lineStyle: {
                  color: '#fff'
                }
              }
            },
            series: [
              {
                data: dataY,
                type: 'bar',
                smooth: true,
                itemStyle: {
                  color: fun[id]()
                },
                barWidth: 10,
                barGap: '30%'
              }
            ]
          }
          // linear-gradient(0deg,rgba(255,210,0,1) 0%,rgba(255,210,0,0.3) 100%);
          this.chart.setOption(option)
        })
        .catch(err => {
          this.$message.error(err.message)
        })
    }
  }
}
