import Vue from 'vue/dist/vue.runtime.esm'
import actionHistory from './../history/actionHistory'
import actionHistoryTitle from './../history/actionHistoryTitle'
import uuid from '@/utils/uuid'
import realTimeDetail from './realTimeDetail'
import { getRealTimeStrategy } from '@/http/api/realTimeApi.js'
export default {
  components: {
    actionHistoryTitle,
    actionHistory,
    realTimeDetail
  },
  data () {
    return {
      showResult: false,
      doConditions: {},
      realTimeListArr: null,
      relateListArr: null
    }
  },
  mounted () {
    Vue.set(this.doConditions, uuid(), null)
  },
  methods: {
    output () {
      if (this.validate()) {
        var arr = []
        for (var i = 0; i < this.$refs.doConditions.length; i++) {
          arr.push(this.$refs.doConditions[i].getData())
        }
        this.featruesData = JSON.stringify(arr)
        let that = this
        getRealTimeStrategy(this.featruesData)
          .then(
            response => {
              that.realTimeListArr = []
              that.relateListArr = []
              that.realTimeListArr.push(response.realtime)
              that.relateListArr.push(response.relate)
              that.showResult = true
            }
          )
          .catch(err => {
            console.log(err)
          })
      }
    },
    addDoCondition () {
      Vue.set(this.doConditions, uuid(), null)
    },
    removeDoCondition (uuid) {
      Vue.delete(this.doConditions, uuid)
    },
    validate () {
      let count = 0
      if (this.$refs.doConditions) {
        for (let item of this.$refs.doConditions) {
          if (!item.validate()) {
            return false
          }
          if (item.inputed()) {
            count++
          }
        }
      }
      if (count) {
        return true
      }
    }
  }
}
