import draggable from 'vuedraggable'
import { getProductCodeByName } from '@/http/api/platformApi.js'
export default {
  components: {
    draggable
  },
  data () {
    return {
      tableIndex: null,
      restaurants: [],
      platformArr: [],
      sendData: '',
      dialog: {
        title: this.$t('create'),
        labelName: this.$t('create'),
        buttonType: 'CREATE'
      },
      createForm: {
        codes: [{}]
      },
      isCode: true
    }
  },

  methods: {
    querySearchAsync (queryString, cb) {
      var restaurants = []
      getProductCodeByName(queryString).then(res => {
        restaurants = res
        var results = queryString
          ? restaurants.filter(this.createStateFilter(queryString))
          : restaurants
        clearTimeout(this.timeout)
        this.timeout = setTimeout(() => {
          cb(results)
        }, 0)
      })
    },
    createStateFilter (queryString) {
      return state => {
        return (
          state.codeName.toLowerCase().indexOf(queryString.toLowerCase()) !== -1
        )
      }
    },
    handleSelect (item) {
      this.createForm.codes[this.tableIndex] = item
    },
    focusInput (index) {
      this.tableIndex = index
    },
    removeDomain (item) {
      let index = this.createForm.codes.indexOf(item)
      this.createForm.codes.splice(index, 1)
      this.$message.success(this.$t('Delsuccess'))
    },
    addDomain () {
      this.createForm.codes.push({})
      this.$message.success(this.$t('Addsuccess'))
    },
    open (item, resultData) {
      this.sendData = resultData
      if (item) {
        this.createForm = {
          codes: item
        }
        this.dialog = {
          title: this.$t('edit'),
          labelName: this.$t('save'),
          buttonType: 'UPDATE'
        }
      } else {
        this.createForm = {
          codes: [{}]
        }
        this.dialog = {
          title: this.$t('create'),
          labelName: this.$t('create'),
          buttonType: 'CREATE'
        }
      }
      this.$refs.hotProductDialog.open()
    },
    onConfirmCreateType (formName) {
      this.$refs[formName].validate(valid => {
        if (valid) {
          let obj = {}
          let isRepeat = false
          let isEmpty = false
          this.createForm.codes.forEach(item => {
            if (obj[item.code]) {
              isRepeat = true
            } else if (!item.code) {
              isEmpty = true
            }
            obj[item.code] = 1
          })
          if (isRepeat) {
            this.$message.error(this.$t('ProductNorepeated'))
            return
          }
          if (isEmpty) {
            this.$message.error(this.$t('ProductNoempty'))
            return
          }
          this.$refs.hotProductDialog.close()
          delete this.createForm.localDate
          if (this.dialog.buttonType === 'CREATE') {
            this.$emit('success', { data: this.createForm, code: 0 })
          } else {
            this.$emit('success', {
              data: this.createForm,
              code: 1,
              dataResource: this.sendData
            })
          }
        } else {
          return false
        }
      })
    }
  }
}
